mapper.select:
select *
from user_login l, user u
where u.userName=#{userEntity.username} and u.id=l.userId and l.logintype=#{userEntity.logintype} and l.password=#{userEntity.password}

mapper.insert:
insert into user_login (id, userId, loginType, 
  password, loginIp, loginTime
  )
values (#{id,jdbcType=INTEGER}, #{userid,jdbcType=VARCHAR}, #{logintype,jdbcType=VARCHAR}, 
  #{password,jdbcType=VARCHAR}, #{loginip,jdbcType=VARCHAR}, #{logintime,jdbcType=VARCHAR}
  )
